const btn=document.getElementById("btn"),body=document.querySelector("body"),table=document.querySelector("table");let dataNames=["Muhammad Hashir","Muhammad Zaid","Muhammad Taha"];let dataAges=[22,25,29];for(let i=0;i<dataNames.length;i++){table.innerHTML+="<tr ><td >"+dataNames[i]+"</td><td >"+dataAges[i]+"</td></tr>"}
btn.addEventListener("click",function(){if(body.style.backgroundColor=="white"){body.style.backgroundColor="black";table.classList.add("dark");table.classList.remove("light");btn.innerText="Light Mode"}else{body.style.backgroundColor="white";btn.innerText="Dark Mode";table.classList.remove("dark");table.classList.add("light")}})